import React from 'react';
import {BrowserRouter as Router, Route, Link, Switch} from 'react-router-dom';
import {makeStyles} from '@material-ui/core/styles';
import { AppBar, Tab, Tabs, Container, Typography,CssBaseline } from '@material-ui/core';
import HomePage from './Views/Home/Home';
import AboutPage from './Views/About/About';
import ProductPage from './Views/Products/ProductsPage';
import CartPage from './Views/Cart/Cart';
import ContactPage from './Views/Cart/Cart';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    },
  layoutComponent: {
    backgroundColor: '#cfe8fc', 
    height: '100vh', 
    padding: '13px',
  },
}));

export default function App() {
  const classes = useStyles();
  return (
    <Router>

        <div className={classes.root}>
          <AppBar position="static">
              <Tabs>
              <Tab label="Home" component={Link} to="/"/>
              <Tab label="Products" component={Link} to="/products"/>
              <Tab label="Cart" component={Link} to="/cart"/>
              <Tab label="Contact" component={Link} to="contact"/>
              <Tab label="About" component={Link} to="about"/>
              </Tabs>
          </AppBar>
       </div>  

       <React.Fragment>
      <CssBaseline />
      <Container maxWidth="lg" fixed>
        <Typography component="div" className={classes.layoutComponent} >
        <Switch>
              <Route exact path="/" component={HomePage}/>
              <Route path="/products" component={ProductPage}/>
              <Route path="/cart" component={CartPage}/>
              <Route path="/contact" component={ContactPage}/>
              <Route path="/about" component={AboutPage}/>
            </Switch>
          </Typography>
      </Container>
    </React.Fragment>



    </Router>
  );
}
